    def find_cycle_length(p, n):
        visited = [0] * (n + 1)
        for i in range(1, n + 1):
            if visited[i] == 0:
                j = i
                cycle = [0] * (n + 1)
                k = 0
                while visited[j] == 0:
                    cycle[k] = j
                    visited[j] = 1
                    j = p[j - 1]
                    k += 1
                cycle_length = k
                for m in range(k):
                    visited[cycle[m]] = 2
                for m in range(n + 1):
