                        j = m
                        while visited[j] == 0:
                            visited[j] = 1
                            j = p[j - 1]
                        if visited[j] == 2:
                            cycle_length = max(cycle_length, k + (cycle_length - m + j - 1) % cycle_length)
                return cycle_length
        return 1

    def find_return_day(p, n):
        cycle_length = find_cycle_length(p, n)
        return_days = []
        for i in range(n):
            return_days.append((i + 1) * cycle_length // n)
        return return_days

