    if N == 0 or M == 0:
        return str((N + M) % mod)

    # dp[i][j] represents the number of possible arrangements with i dogs and j monkeys
    dp = [[0] * (M + 1) for _ in range(N + 1)]

    # Base cases
    for i in range(N + 1):
        dp[i][0] = 1
    for j in range(M + 1):
        dp[0][j] = 1

    # Calculate all possible arrangements
    for i in range(1, N + 1):
