def solution(stdin: str) -> str:
    def preprocess_tree(n: int, edges: list) -> tuple:
        parents = [0] * (n + 1)
        depth = [0] * (n + 1)
        children = [[] for _ in range(n + 1)]

        for u, v, w in edges:
            parents[v] = u
            depth[v] = depth[u] + 1
            children[u].append(v)

        return parents, depth, children

    def count_pairs(parents: list, depth: list, children: list, n: int, q: int) -> int:
        result = 0
        for u in range(1, n + 1):
            for v in range(u + 1, n + 1):
                if abs(depth[u] - depth[v]) % 2 == 1:
                    continue
                common_parent = lca(u, v, parents, depth)
                max_weight = float('-inf')
                if common_parent == u:
                    max_weight = max(max_weight, get_max_weight(children[u], children[v], q))
                if common_parent == v:
                    max_weight = max(max_weight, get_max_weight(children[v], children[u], q))
                if max_weight != float('-inf'):
                    result += max_weight
        return result

    def lca(u: int, v: int, parents: list, depth: list) -> int:
        while u != v:
            if depth[u] > depth[v]:
                u = parents[u]
            else:
                v = parents[v]
        return u

    def get_max_weight(children_u: list, children_v: list, q: int) -> int:
        max_weight = 0
        for w in [0] * 3:
            max_weight = max(max_weight, dfs(children_u, children_v, w, q))
        return max_weight

    def dfs(children_u: list, children_v: list, w: int, q: int) -> int:
        max_weight = 0
        for child in children_u:
            weight = w + 1 if child in children_v else w
            if weight <= q:
                max_weight = max(max_weight, dfs(children_u, children_v, weight, q))
            else:
                max_weight = max(max_weight, weight)
        return max_weight

    def main(stdin: str) -> str:
        n, m, edges, qs = parse_input(stdin)
        parents, depth, children = preprocess_tree(n, edges)
        return ' '.join(str(count_pairs(parents, depth, children, n, q)) for q in qs)

    def parse_input(stdin: str) -> tuple:
        lines = stdin.split('\n')
        n, m = map(int, lines[0].split())
        edges = [tuple(map(int, line.split())) for line in lines[1:n]]
        qs = list(map(int, lines[-1].split()))
        return n, m, edges, qs

    return main(stdin)

