        return 1 <= i <= H and 1 <= j <= W

    def dfs(i: int, j: int, target: str) -> bool:
        if not is_valid(i, j):
            return False

        if grid[i - 1][j - 1] == target:
            return True

        if grid[i - 1][j - 1] != '.':
            return False

        grid[i - 1][j - 1] = target

        for di, dj in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            if dfs(i + di, j + dj, target):
                return True

        grid[i - 1][j - 1] = '.'
        return False

    result = 'No'
    for i in range(H):
        for j in range(W):
            if dfs(i + 1, j + 1, grid[i][j]):
                result = 'Yes'
                break

        if result == 'Yes':
            break

    return result

