    from heapq import heappush, heappop
    from sys import setrecursionlimit

    setrecursionlimit(10**7)

    n, m, k = map(int, stdin.split('\n')[0].split())
    roads = [list(map(int, stdin.split('\n')[i].split())) for i in range(1, m + 1)]
    courier_routes = [list(map(int, stdin.split('\n')[i + m].split())) for i in range(k)]

    adj_list = [[] for _ in range(n + 1)]
    for x, y, w in roads:
        adj_list[x].append((y, w))
        adj_list[y].append((x, w))

    def dijkstra(src, target):
        dist = [float('inf')] * (n + 1)
        dist[src] = 0
        pq = []
        heappush(pq, (0, src))
        while pq:
            d, u = heappop(pq)
            if u == target:
                return d
            if d > dist[u]:
                continue
            for v, w in adj_list[u]:
                new_dist = dist[u] + w
                if new_dist < dist[v]:
                    dist[v] = new_dist
                    heappush(pq, (new_dist, v))
        return float('inf')

    def find_min_cost_route(i):
        a, b = courier_routes[i]
        return dijkstra(a, b)

    min_cost = float('inf')
    for i in range(k):
        min_cost = min(min_cost, find_min_cost_route(i))

    return str(min_cost)

