    def is_satisfied(a, b, num_kids, kids_with_a_plus_1):
        return (b - a <= 1) and (kids_with_a_plus_1 <= num_kids // 2)

    def find_max_candies(n, k):
        if n < k:
            return 0
        if k == 1:
            return n
        if k == 2:
            return n // 2

        # Binary search for the maximum number of candies
        left, right = 0, n
        while left <= right:
            mid = (left + right) // 2
            num_kids_with_mid_candies = n - mid * (k - 1)
            kids_with_mid_candies = num_kids_with_mid_candies // k
            kids_with_mid_candies_plus_1 = num_kids_with_mid_candies % k

            if is_satisfied(mid, mid + kids_with_mid_candies, k, kids_with_mid_candies_plus_1):
                left = mid + 1
            else:
                right = mid - 1

        return right

    t = int(stdin.readline())
    output = []
    for _ in range(t):
        n, k = map(int, stdin.readline().split())
        max_candies = find_max_candies(n, k)
        output.append(str(max_candies))

    return "\n".join(output)

