    def move(board: list, direction: int) -> tuple:
        if direction == 0: # left
            for i in range(4):
                board[i] = move_left(board[i])
        elif direction == 1: # up
            for i in range(4):
                board[i] = move_up(board[i])
        elif direction == 2: # right
            for i in range(4):
                board[i] = move_right(board[i])
        elif direction == 3: # down
            for i in range(4):
                board[i] = move_down(board[i])
        return board

    def move_left(row: list) -> list:
        row = [num for num in row if num != 0]
        for i in range(len(row) - 1):
            if row[i] == row[i + 1]:
                row[i] *= 2
                row[i + 1] = 0
        row = [num for num in row if num != 0]
        row += [0] * (4 - len(row))
        return row

    def move_up(row: list) -> list:
        return move_left([row[i] for i in [0, 1, 2, 3]])

    def move_right(row: list) -> list:
        return move_left(row[::-1])[::-1]

    def move_down(row: list) -> list:
        return move_up(row[::-1])[::-1]

    def print_board(board: list) -> str:
        output = ''
        for row in board:
            output += ' '.join(map(str, row)) + '\n'
        return output.strip()

    lines = stdin.strip().split('\n')
    board = [[int(num) for num in line.split()] for line in lines[:-1]]
    direction = int(lines[-1])

    board = move(board, direction)
    return print_board(board)

