    pos = input_list[-1]

    # Create a linked list
    class ListNode:
        def __init__(self, val=0, next=None):
            self.val = val
            self.next = next

    dummy = ListNode(0)
    curr = dummy
    for val in head:
        curr.next = ListNode(val)
        curr = curr.next

    # Check for cycle
    has_cycle = False
    slow = dummy.next
    fast = dummy.next
    while fast and fast.next:
        slow = slow.next
        fast = fast.next.next
        if slow == fast:
            has_cycle = True
            break

    # If cycle exists, check if pos is part of the cycle
    if has_cycle:
        cycle_start = slow
        while True:
            if cycle_start == fast:
                break
            cycle_start = cycle_start.next
            fast = fast.next
        if pos == -1:
            return "true"
        curr = dummy.next
        for _ in range(pos):
            curr = curr.next
        if curr in cycle_start:
            return "true"
        else:
            return "false"
    else:
        return "false"

