    digits = [int(d) for d in str(x)]
    n = len(digits)

    # Find the index of the first digit that is less than the previous digit
    index = n - 1
    while index > 0 and digits[index] <= digits[index - 1]:
        index -= 1

    if index == 0:
        return "0"

    # Swap the digits at index and index - 1
    digits[index - 1], digits[index] = digits[index], digits[index - 1]

    # Reverse the digits after index
    digits[index:] = digits[n - 1:index - 1:-1]

    result = int("".join(str(d) for d in digits))

    if result <= x:
        return "0"
    else:
        return str(result)

