        A3 = area(x1, y1, x2, y2, x, y)
        return abs(A - (A1 + A2 + A3)) < 1e-9

    def num_trees_inside(x1, y1, x2, y2, x3, y3, trees):
        count = 0
        for x, y in trees:
            if is_inside(x1, y1, x2, y2, x3, y3, x, y):
                count += 1
