    def is_street_closed(street_start, street_end, closed_streets):
        for start, end in closed_streets:
            if start == street_start and end == street_end:
                return True
        return False

    def calculate_min_time(start_intersection, end_intersection, k, g, streets):
        visited_intersections = {start_intersection}
        visited_streets = []
        closed_streets = []

        for i in range(g):
            if i < k:
                closed_streets.append((streets[i][0], streets[i][1]))
            else:
                closed_streets.append((streets[i][1], streets[i][0]))

        for i in range(k + g, len(streets)):
            if streets[i][0] == start_intersection:
                start_street = streets[i]
                break

        current_intersection = start_street[1]
        visited_intersections.add(current_intersection)
        visited_streets.append(start_street)

        while current_intersection != end_intersection:
            possible_streets = [street for street in streets if street[0] == current_intersection and street not in visited_streets and not is_street_closed(street[0], street[1], closed_streets)]

            if not possible_streets:
                return -1  # No possible route

            shortest_street = min(possible_streets, key=lambda x: x[2])
            visited_streets.append(shortest_street)
            visited_intersections.add(shortest_street[1])
            current_intersection = shortest_street[1]

        return sum(street[2] for street in visited_streets)

    lines = stdin.strip().split('\n')
    N, M = map(int, lines[0].split())
    A, B, K, G = map(int, lines[1].split())
    streets = [list(map(int, line.split())) for line in lines[2:]]

    min_time = calculate_min_time(A, B, K, G, streets)

    return str(min_time)

