            beta = angle(b * a, d * c, abs(b - d))
            scale = scale_factor(polygon, area)
            new_polygon.append((a * math.cos(alpha) + b * math.sin(beta), b * math.cos(alpha) + a * math.sin(beta)))
        return new_polygon

