    holes = stdin.split("\n")[1:]

    def cheese_weight(z, r, x, y, z_max):
        if z + r <= z_max:
            return 0
        if z - r >= z_max:
            return ((2 * r) ** 2) * (z_max - z + r)
        return ((2 * r) ** 2) * (z_max - z + r) + (4 * (r ** 3) / 3) * (1 - (z_max - z) / (2 * r))

    def cheese_slice_weight(slice_thickness):
        total_weight = 0
        for hole in holes:
            r, x, y, z = map(int, hole.split())
            total_weight += cheese_weight(z, r, x, y, slice_thickness)
        return total_weight

    def binary_search(target_weight, precision):
        low, high = 0, 100000
        while high - low > precision:
            mid = (low + high) / 2
            if cheese_slice_weight(mid) < target_weight:
                low = mid
            else:
                high = mid
        return (low + high) / 2

    target_weight = cheese_weight(0, 10000, 0, 0, 100000) / s
    precision = 100000 / (10 ** 6)
    thicknesses = [binary_search(target_weight, precision) for _ in range(s)]

    return "\n".join(f"{thickness:.12f}" for thickness in thicknesses)

