        dfs(i + 1, j, visited)
        dfs(i, j - 1, visited)
        dfs(i, j + 1, visited)

    def count_islands():
        islands = 0
        visited = set()
        for i in range(r):
            for j in range(c):
                if (i, j) not in visited and image[i][j] == 'L':
                    dfs(i, j, visited)
                    islands += 1
        return islands

    stdin = stdin.split('\n')
    r, c = map(int, stdin[0].split())
    image = [list(row) for row in stdin[1:]]

    return str(count_islands())

