def solution(stdin: str) -> str:
    a, b = map(int, stdin.split())

    if a == b:
        return 0

    operations = 0

    while a != b:
        if a % 2 == 0 and a // 2 >= b:
            a //= 2
        else:
            a += 1
        operations += 1

    return str(operations)
