    def find_parent(parent, u):
        if parent[u] == -1:
            return u
        parent[u] = find_parent(parent, parent[u])
        return parent[u]

    def union_parent(parent, x, y):
        x = find_parent(parent, x)
        y = find_parent(parent, y)
        if x != y:
            parent[y] = x

    parent = [-1] * (n + 1)
    for u, v in edges:
        union_parent(parent, u, v)

    def dfs(u, depth):
        res = 0
        for v, w in edges:
            if v == u:
                continue
            if find_parent(parent, u) == find_parent(parent, v):
                continue
            res += dfs(v, depth + 1)
        return res + depth

    a, b, c = 1, 1, 1
    res = -1
    for i in range(1, n + 1):
        temp_res = dfs(i, 0)
        if temp_res > res:
            a, b, c = i, 1, 1
            res = temp_res

    for i in range(1, n + 1):
        if i != a:
            b = i
            break

    for i in range(1, n + 1):
        if i != a and i != b:
            c = i
            break

    return f"{res}\n{a} {b} {c}\n"

