    def is_regular(bracket: str) -> bool:
        stack = []
        for char in bracket:
            if char == "(":
                stack.append(char)
            else:
                if not stack or stack.pop() != "(":
                    return False
        return len(stack) == 0

    def count_regular_brackets(s: str, n: int, index: int, count: int) -> int:
        if index == 2 * n:
            return count if is_regular(s) else 0
        if s[index:index + len(s)] == s:
            return count_regular_brackets(s + "(", n, index + 1, count)
        return count_regular_brackets(s + "(", n, index + 1, count) + count_regular_brackets(s + ")", n, index + 1, count)

    return str(count_regular_brackets("", n, 0, 0) % (10**9 + 7))

def solution(stdin: str) -> str:
    n = int(stdin)

    def is_divisible_by_25(number: int) -> bool:
        return number % 25 == 0

    def is_leading_zero(number: int) -> bool:
        return int(str(number)[0]) == 0

    def is_possible(number: int) -> bool:
        if is_leading_zero(number) or not is_divisible_by_25(number):
            return False
        return True

    def swap_adjacent_digits(number: int, index: int) -> int:
        number_str = str(number)
        if index == len(number_str) - 1:
            return number
        swapped_digit = list(number_str)
        swapped_digit[index], swapped_digit[index + 1] = swapped_digit[index + 1], swapped_digit[index]
        return int("".join(swapped_digit))

    def find_min_moves(number: int) -> int:
        if is_possible(number):
            return 0

        min_moves = float("inf")
        for i in range(len(str(number)) - 1):
            next_number = swap_adjacent_digits(number, i)
            if is_possible(next_number):
                return 1 + find_min_moves(next_number)
            else:
                moves = 1 + find_min_moves(next_number)
                min_moves = min(min_moves, moves)

        return -1 if min_moves == float("inf") else min_moves

    return find_min_moves(n)

