    n = int(stdin.split("\n")[0])
    numbers = list(map(int, stdin.split("\n")[1].split()))
    matrix = []

    def is_palindromic(matrix):
        for i in range(n):
            for j in range(n):
                if matrix[i][j] != matrix[n - i - 1][j] or matrix[i][j] != matrix[i][n - j - 1]:
                    return False
        return True

    def backtrack(index):
        if index == n * n:
            if is_palindromic(matrix):
                return True
            return False

        row = index // n
        col = index % n

        for num in numbers:
            if num not in matrix[row] and num not in [matrix[i][col] for i in range(n)]:
                matrix[row].append(num)
                numbers.remove(num)
                if backtrack(index + 1):
                    return True
                numbers.append(num)
                matrix[row].pop()

        return False

    for i in range(n):
        matrix.append([])

    backtrack(0)

    if not matrix:
        return "NO"

    output = ["YES"]
    for row in matrix:
        output.append(" ".join(str(num) for num in row))

    return "\n".join(output)

