    def get_abbreviation_length(words, start, end):
        abbreviation = "".join([w[0].upper() for w in words[start:end]])
        return len(abbreviation)

    def find_equal_segments(words, start, end, segments):
        for i in range(start, end):
            for j in range(i + 1, end):
                if (i, j) in segments:
                    continue
                if len(words[i]) != len(words[j]):
                    continue
                equal = True
                for k in range(len(words[i])):
                    if words[i][k] != words[j][k]:
                        equal = False
                        break
                if equal:
                    segments.add((i, j))

    def min_length_with_abbreviation(words, segments):
        n = len(words)
        dp = [0] * (n + 1)
        for i in range(n - 1, -1, -1):
            dp[i] = dp[i + 1] + 1
            for j in range(i + 1, n):
                if (i, j) in segments:
                    abbreviation_length = get_abbreviation_length(words, i, j)
                    dp[i] = min(dp[i], dp[j + 1] + abbreviation_length)
        return dp[0]

    lines = stdin.splitlines()
    n = int(lines[0])
    words = lines[1].split()

    segments = set()
    find_equal_segments(words, 0, n, segments)

    return min_length_with_abbreviation(words, segments)

