    prev_operand = None
    current_operand = None
    operator = None

    # Iterate through the tokens and evaluate the expression
    for token in tokens:
        if token.isdigit():
            current_operand = int(token)
        else:
            operator = token

        if prev_operand is not None and current_operand is not None:
            if operator == '+':
                prev_operand += current_operand
            else:
                prev_operand -= current_operand

        prev_operand = current_operand
        current_operand = None

