    n, m, k = map(int, stdin.split()[:3])
    roads = [list(map(int, stdin.split()[3:])) for _ in range(m)]

    def find_roads(src, dest, visited, path):
        if src == dest:
            return path
        for i, road in enumerate(roads):
            if i not in visited and (road[0] == src or road[1] == src):
                if road[0] != src:
                    src, dest = dest, src
                visited.add(i)
                if find_roads(dest, src, visited, path + '1'):
                    return True
                visited.remove(i)
        return False

    def generate_options(t, k, options):
        if t == k:
            return options
        for i in range(m):
            new_options = []
            for option in options:
                if option[i] == '1':
                    new_options.append(option)
            if len(new_options) == 0:
                for j in range(m):
                    if j not in [x for x in range(i)]:
                        new_option = option[:i] + '1' + option[i + 1:]
                        new_options.append(new_option)
            if len(options) + len(new_options) <= k:
                options.extend(new_options)
            else:
                for _ in range(k - len(options)):
                    options.append(new_options.pop())
                return options
        return options

    options = ['0' * m]
    for i in range(1, n):
        if not find_roads(1, i, set(), ''):
            return '0'

    options = generate_options(t, k, options)
    return str(len(options)) + '\n' + '\n'.join(options)

