        while len(A) > 1:
            a1, a2 = 0, 0
            min_diff = float('inf')
            for i in range(len(A)):
                for j in range(len(A)):
                    if i != j:
                        diff = attack(A[i], A[j])
                        if diff < min_diff:
                            min_diff = diff
                            a1, a2 = i, j
            A[a1] = attack(A[a1], A[a2])
            if A[a1] > 0:
                A[a2] = 0
