    def dfs(i: int, j: int, xor_sum: int) -> int:
        if i == n - 1 and j == m - 1:
            return int(xor_sum == k)
        if i < 0 or i >= n or j < 0 or j >= m:
            return 0

        right = dfs(i, j + 1, xor_sum ^ a[i][j])
        bottom = dfs(i + 1, j, xor_sum ^ a[i][j])
        return right + bottom

    return str(dfs(0, 0, 0))

