    n = int(stdin.split("\n")[0])
    a = list(map(int, stdin.split("\n")[1].split()))

    # Define a helper function to check if two arrays are equal up to a certain index
    def equal_up_to_index(arr1, arr2, index):
        for i in range(index + 1):
            if arr1[i] != arr2[i]:
                return False
        return True

    # Define a helper function to check if two arrays have consecutive equal elements
    def consecutive_equals(arr1, arr2):
        for i in range(1, n):
            if arr1[i] == arr1[i - 1] and arr2[i] == arr2[i - 1]:
                return True
        return False

    # Define a helper function to check if two arrays are monotonic renumerations of each other
    def is_monotonic_renumeration(arr1, arr2):
        return equal_up_to_index(arr1, arr2, n - 1) and consecutive_equals(arr1, arr2)

    # Initialize a list to store all possible monotonic renumerations
    monotonic_renumerations = []

    # Iterate through all possible arrays
    for i in range(2 ** n):
        # Convert binary representation of i to a list of 0s and 1s
        binary_representation = [int(x) for x in bin(i)[2:].zfill(n)]

        # Check if the binary representation is a valid monotonic renumeration
        is_valid = True
        for j in range(1, n):
            if binary_representation[j] == binary_representation[j - 1]:
                is_valid = False
                break
        if is_valid:
            # Convert the binary representation to an array of integers
            renumeration = [binary_representation[0]]
            for j in range(1, n):
                renumeration.append(renumeration[-1] + binary_representation[j])

            # Check if the renumeration is not already in the list
            already_exists = False
            for j in range(len(monotonic_renumerations)):
                if is_monotonic_renumeration(renumeration, monotonic_renumerations[j]):
                    already_exists = True
                    break
            if not already_exists:
                monotonic_renumerations.append(renumeration)

    # Calculate the answer modulo 998244353
    mod = 998244353
    answer = len(monotonic_renumerations)
    while answer >= mod:
        answer -= mod
    return str(answer)

