def solution(stdin: str) -> str:
    def find_cycle(next_kids, kids):
        visited = [False] * n
        i = 0
        cycle = []
        while not visited[i]:
            visited[i] = True
            cycle.append(i)
            i = next_kids[i]
        return cycle

    def find_start_kid(next_kids, kids, start_kid):
        if start_kid in kids:
            return start_kid
        for kid in kids:
            if next_kids[kid] == start_kid:
                return kid

    def dfs(next_kids, kids, start_kid, visited, cycle, result):
        if len(result) == n:
            return True
        if visited[start_kid]:
            return False
        visited[start_kid] = True
        result.append(start_kid)
        for next_kid in kids[start_kid]:
            if dfs(next_kids, kids, next_kid, visited, cycle, result):
                return True
        result.pop()
        return False

    n = int(stdin.readline().strip())
    next_kids = [0] * n
    kids = {}
    for i in range(n):
        a1, a2 = map(int, stdin.readline().strip().split())
        next_kids[i] = a1
        kids[i] = (a1, a2)

    cycle = find_cycle(next_kids, kids)
    start_kid = find_start_kid(next_kids, kids, cycle[0])
    visited = [False] * n
    result = []
    dfs(next_kids, kids, start_kid, visited, cycle, result)
    return " ".join(map(str, result))

