    n, m = map(int, stdin.split())
    a = list(map(int, stdin.split()))
    special_offers = []
    for i in range(m):
        x, y, w = map(int, stdin.split())
        special_offers.append((x, y, w))

    def kruskal(n, a, special_offers):
        parent = [i for i in range(n)]
        rank = [0] * n
        special_offers.sort(key=lambda x: x[2])

        def find(u):
            if u == parent[u]:
                return u
            parent[u] = find(parent[u])
            return parent[u]

        def union(u, v):
            u = find(u)
            v = find(v)
            if u == v:
                return False
            if rank[u] > rank[v]:
                parent[v] = u
            else:
                parent[u] = v
                if rank[u] == rank[v]:
                    rank[v] += 1
            return True

        cost = 0
        for x, y, w in special_offers:
            if union(x - 1, y - 1):
                cost += w
        return cost

    return str(kruskal(n, a, special_offers))

