def solution(stdin: str) -> str:
    H, W = map(int, stdin.readline().split())
    matrix = []
    for _ in range(H):
        matrix.append(list(stdin.readline().strip()))

    def is_all_white(lst):
        return all(x == '.' for x in lst)

    def remove_rows_cols(matrix):
        while True:
            # Check rows
            row_to_remove = None
            for i in range(H):
                if is_all_white(matrix[i]):
                    row_to_remove = i
                    break
            if row_to_remove is not None:
                del matrix[row_to_remove]
                H -= 1
                for i in range(H):
                    del matrix[i][row_to_remove]
                continue

            # Check columns
            col_to_remove = None
            for j in range(W):
                if is_all_white([matrix[i][j] for i in range(H)]):
                    col_to_remove = j
                    break
            if col_to_remove is not None:
                for i in range(H):
                    del matrix[i][col_to_remove]
                W -= 1
                continue

            break

    remove_rows_cols(matrix)

    output = ''
    for row in matrix:
        output += ''.join(row) + '\n'
    return output[:-1]

