    def binary_search(arr, required_sum):
        left, right = 0, len(arr)
        while left < right:
            mid = (left + right) // 2
            if sum(sorted(arr, reverse=True)[:mid]) >= required_sum:
                right = mid
            else:
                left = mid + 1
        return left

    n, m = map(int, stdin.readline().split())
    sizes = [list(map(int, stdin.readline().split())) for _ in range(n)]
    sizes.sort(key=lambda x: x[1] - x[0], reverse=True)

    required_sum = 0
    for i in range(n):
        required_sum += sizes[i][0]

    if required_sum <= m:
        return "0"

    min_songs_to_compress = binary_search(sizes, m)
    return str(min_songs_to_compress) if min_songs_to_compress < n else "-1"

