def solution(stdin: str) -> str:
    W, H, x, y = map(int, stdin.split())

    # Find the area of the rectangle
    area = W * H

    # If the point is on a vertex, return the area of the rectangle
    if (x, y) in [(0, 0), (W, 0), (W, H), (0, H)]:
        return f"{area} 0\n"

    # Check if the point is on the border
    is_on_border = False
    if x == 0 or x == W or y == 0 or y == H:
        is_on_border = True

    # Calculate the maximum area of the part not larger than the other
    if is_on_border:
        if x == 0:
            max_area = y * W
        elif x == W:
            max_area = (H - y) * W
        elif y == 0:
            max_area = x * H
        else:
            max_area = (W - x) * H
    else:
        max_area = min(W * y, H * x)

    # Check if there are multiple ways to cut the rectangle
    is_unique = True
    if is_on_border:
        if x == 0 or x == W:
            if y * W == max_area:
                is_unique = False
        else:
            if (H - y) * W == max_area:
                is_unique = False
    else:
        if max_area == W * y:
            is_unique = False
        elif max_area == H * x:
            is_unique = False

    # Return the result
    if is_unique:
        return f"{max_area} 0\n"
    else:
        return f"{max_area} 1\n"

