def solution(stdin: str) -> str:
    n, m = map(int, stdin.readline().split())
    adj_list = [[] for _ in range(n + 1)]
    visited = [False] * (n + 1)
    cycles = 0

    for _ in range(m):
        v, u = map(int, stdin.readline().split())
        adj_list[v].append(u)
        adj_list[u].append(v)

    def dfs(node: int, parent: int) -> None:
        visited[node] = True
        for neighbor in adj_list[node]:
            if not visited[neighbor]:
                dfs(neighbor, node)

    def is_cycle(node: int, parent: int) -> bool:
        for neighbor in adj_list[node]:
            if neighbor != parent:
                if node in adj_list[neighbor] and node != neighbor:
                    return True
                if is_cycle(neighbor, node):
                    return True
        return False

    for i in range(1, n + 1):
        if not visited[i]:
            dfs(i, -1)
            if is_cycle(i, -1):
                cycles += 1

    return str(cycles)

def solution(stdin: str) -> str:
    def is_nice(vertex1, vertex2, colored_vertices, components):
        for vertex in (vertex1, vertex2):
            if vertex in colored_vertices:
                colored_vertices.remove(vertex)
            else:
                component = components.pop(vertex)
                colored_vertices = colored_vertices.intersection(component)
                if not colored_vertices:
                    return True
        return False

    def count_nice_edges(n, colors, edges):
        colored_vertices = set()
        for i, color in enumerate(colors):
            if color:
                colored_vertices.add(i + 1)

        components = {}
        for vertex1, vertex2 in edges:
            if vertex1 not in components:
                components[vertex1] = {vertex1}
            components[vertex1].add(vertex2)
            if vertex2 not in components:
                components[vertex2] = {vertex2}
            components[vertex2].add(vertex1)

        nice_edges = 0
        for vertex1, vertex2 in edges:
            if is_nice(vertex1, vertex2, colored_vertices.copy(), components.copy()):
                nice_edges += 1
        return nice_edges

    input_data = [line.strip() for line in stdin.split('\n') if line.strip()]
    n = int(input_data[0])
    colors = [int(color) for color in input_data[1].split()]
    edges = [(int(v1), int(v2)) for v1, v2 in (line.split() for line in input_data[2:])]

    result = count_nice_edges(n, colors, edges)
    return str(result)

