    k = int(k)

    # Find the index of the first different character
    for i in range(k):
        if s[i] != t[i]:
            break

    # If s and t are the same string, return s
    if i == k - 1 and s[i] == t[i]:
        return s

    # Generate the list of all strings between s and t
    alphabet = 'abcdefghijklmnopqrstuvwxyz'
    start = alphabet.index(s[i])
    end = alphabet.index(t[i])
    middle = (start + end) // 2

    if middle < start or middle > end:
        middle = start
    else:
        middle = alphabet[middle]

    return s[:i] + middle + s[i + 1:]

