def solution(stdin: str) -> str:
    def find(parents, i):
        if parents[i] == i:
            return i
        return find(parents, parents[i])

    def union(parents, rank, x, y):
        xroot = find(parents, x)
        yroot = find(parents, y)

        if xroot == yroot:
            return False

        if rank[xroot] < rank[yroot]:
            parents[xroot] = yroot
        elif rank[xroot] > rank[yroot]:
            parents[yroot] = xroot
        else:
            parents[yroot] = xroot
            rank[xroot] += 1
        return True

    def kruskal(edges):
        parents = [i for i in range(n)]
        rank = [0] * n
        mst_cost = 0
        mst_edges = []

        for edge in sorted(edges, key=lambda x: x[2]):
            u, v, w = edge
            if union(parents, rank, u - 1, v - 1):
                mst_cost += w
                mst_edges.append(edge)

        return mst_cost, mst_edges

    def check_unique_mst(mst_cost, mst_edges, edges):
        for i in range(len(mst_edges)):
            test_edges = mst_edges.copy()
            test_edge = test_edges[i]
            test_edges.remove(test_edge)
            u, v, w = test_edge

            for j in range(len(edges)):
                if edges[j][0] == u and edges[j][1] == v and edges[j][2] == w:
                    edges[j][2] += 1
                    test_cost, test_mst_edges = kruskal(edges)
                    edges[j][2] -= 1

                    if test_cost == mst_cost:
                        return False
            test_edges.append((u, v, w + 1))
            test_cost, test_mst_edges = kruskal(test_edges)

            if test_cost == mst_cost:
                return False
        return True

    n, m = map(int, stdin.readline().split())
    edges = [list(map(int, stdin.readline().split())) for _ in range(m)]

    mst_cost, mst_edges = kruskal(edges)

    if check_unique_mst(mst_cost, mst_edges, edges):
        print(0)
    else:
        print(1)

