    def find(parent, i):
        if parent[i] == i:
            return i
        return find(parent, parent[i])

    def union(parent, rank, x, y):
        xroot = find(parent, x)
        yroot = find(parent, y)

        if xroot == yroot:
            return False

        if rank[xroot] < rank[yroot]:
            parent[xroot] = yroot
        elif rank[xroot] > rank[yroot]:
            parent[yroot] = xroot
        else:
            parent[yroot] = xroot
            rank[xroot] += 1
        return True

    def kruskal(n, edges):
        parent = [i for i in range(n + 1)]
        rank = [0] * (n + 1)
        result = []

        edges.sort(key=lambda x: x[2], reverse=True)

        for edge in edges:
            if union(parent, rank, edge[0], edge[1]):
                result.append(edge)

        return result

    def get_max_degree(n, edges):
        degrees = [0] * (n + 1)
        for edge in edges:
            degrees[edge[0]] += 1
            degrees[edge[1]] += 1
        return max(degrees[1:])

