    def dfs(node, parent, depth):
        global max_depth, vertex_depth
        max_depth = max(max_depth, depth)
        vertex_depth[node] = depth
        for neighbor, weight in tree[node]:
            if neighbor != parent:
                dfs(neighbor, node, depth + weight)

    # Dijkstra's Algorithm
    def dijkstra(source, target_depth):
        dist = [float('inf')] * (n + 1)
        dist[source] = 0
        visited = [False] * (n + 1)
        heap = [(0, source)]

        while heap:
            d, node = heapq.heappop(heap)
            if visited[node]:
                continue
            visited[node] = True

            for neighbor, weight in tree[node]:
                if not visited[neighbor]:
                    new_dist = d + weight
                    if new_dist < dist[neighbor]:
                        dist[neighbor] = new_dist
                        heapq.heappush(heap, (new_dist, neighbor))

        count = 0
        for i in range(1, n + 1):
            if not visited[i] and vertex_depth[i] <= target_depth:
                count += sum(1 for neighbor, weight in tree[i] if not visited[neighbor])
        return count

