    def dijkstra(graph, start, target):
        queue = []
        visited = set()
        nodes = list(graph.keys())
        dist = {node: float('inf') for node in nodes}
        dist[start] = 0
        heapq.heappush(queue, (0, start))

        while queue:
            (d, node) = heapq.heappop(queue)
            if node in visited:
                continue
            visited.add(node)
            if node == target:
                return dist[target]

            for neighbor, cost in graph[node].items():
                if neighbor not in visited:
                    alt = dist[node] + cost
                    if alt < dist[neighbor]:
                        dist[neighbor] = alt
                        heapq.heappush(queue, (alt, neighbor))

        return None

    lines = stdin.split('\n')
    n, m, k = map(int, lines[0].split())
    graph = {i: {} for i in range(1, n + 1)}
    roads = []

    for i in range(1, m + 1):
        x, y, w = map(int, lines[i].split())
        roads.append((x, y, w))
        graph[x][y] = w
        graph[y][x] = w

    routes = []
    for i in range(m + 1, m + k + 1):
        a, b = map(int, lines[i].split())
        routes.append((a, b))

    min_cost = float('inf')

    for x, y, w in roads:
        graph[x][y] = 0
        graph[y][x] = 0

        total_cost = sum(dijkstra(graph, route[0], route[1]) for route in routes)
        min_cost = min(min_cost, total_cost)

        graph[x][y] = w
        graph[y][x] = w

    return str(min_cost)

