def solution(stdin: str) -> str:
    def is_lexicographically_less(p: List[int], q: List[int]) -> bool:
        n = len(p)
        for i in range(n):
            if p[i] != q[i]:
                return p[i] < q[i]
        return False

    def find_min_permutation(permutation: List[int]) -> List[int]:
        n = len(permutation)
        for i in range(n - 1):
            if i + 1 < n and permutation[i] > permutation[i + 1]:
                permutation[i], permutation[i + 1] = permutation[i + 1], permutation[i]
                return permutation
        return permutation

    def process_test_case(permutation: List[int]) -> List[int]:
        min_permutation = find_min_permutation(permutation)
        while is_lexicographically_less(min_permutation, permutation):
            permutation = min_permutation[:]
            min_permutation = find_min_permutation(min_permutation)
        return min_permutation

    q = int(stdin.readline().strip())
    result = []
    for _ in range(q):
        n = int(stdin.readline().strip())
        permutation = [int(x) for x in stdin.readline().strip().split()]
        result.append(" ".join(str(x) for x in process_test_case(permutation)))

    return "\n".join(result)

