def solution(stdin: str) -> str:
    def move(matrix, direction):
        if direction == 0:
            matrix = [list(reversed(row)) for row in matrix]
            matrix, _ = move_left(matrix)
            matrix = [list(reversed(row)) for row in matrix]
        elif direction == 1:
            matrix = [list(reversed(row)) for row in matrix]
            matrix = list(zip(*matrix))
            matrix, _ = move_left(matrix)
            matrix = list(zip(*matrix))
            matrix = [list(reversed(row)) for row in matrix]
        elif direction == 2:
            matrix, _ = move_left(matrix)
        else:
            matrix = [list(reversed(row)) for row in matrix]
            matrix = list(zip(*matrix))
            matrix, _ = move_left(matrix)
            matrix = list(zip(*matrix))
            matrix = [list(reversed(row)) for row in matrix]
        return matrix

    def move_left(matrix):
        new_matrix = []
        moved = False
        for row in matrix:
            new_row = []
            i = 0
            while i < len(row):
                if row[i] == 0:
                    i += 1
                else:
                    new_row.append(row[i])
                    if i < len(row) - 1 and row[i] == row[i + 1]:
                        new_row.append(0)
                        moved = True
                        i += 2
                    else:
                        i += 1
            while len(new_row) < len(row):
                new_row.append(0)
            new_matrix.append(new_row)
        return new_matrix, moved

    lines = stdin.strip().split('\n')
    matrix = [list(map(int, line.strip().split())) for line in lines[:-1]]
    direction = int(lines[-1])

    new_matrix = move(matrix, direction)

    return '\n'.join([' '.join(list(map(str, row))) for row in new_matrix])

def solution(stdin: str) -> str:
    input_molecule, k = stdin.split()
    k = int(k)
    output_molecule = input("Enter the desired output molecule: ")

    def parse_molecule(molecule: str) -> dict:
        atoms = {}
        i = 0
        while i < len(molecule):
            atom = molecule[i]
            i += 1
            if i < len(molecule) and molecule[i].isdigit():
                count = ""
                while i < len(molecule) and molecule[i].isdigit():
                    count += molecule[i]
                    i += 1
                count = int(count)
            else:
                count = 1
            if atom in atoms:
                atoms[atom] += count
            else:
                atoms[atom] = count
        return atoms

    input_atoms = parse_molecule(input_molecule)
    output_atoms = parse_molecule(output_molecule)

    def lcm(a: int, b: int) -> int:
        return a * b // gcd(a, b)

    def gcd(a: int, b: int) -> int:
        while b:
            a, b = b, a % b
        return a

    def get_n(input_atoms: dict, output_atoms: dict, k: int) -> int:
        if input_atoms == output_atoms:
            return k

        lcm_values = []
        for atom in input_atoms:
            if atom in output_atoms:
                lcm_values.append(lcm(input_atoms[atom], output_atoms[atom]))
        if not lcm_values:
            return 0

        lcm_value = lcm_values[0]
        for i in range(1, len(lcm_values)):
            lcm_value = lcm(lcm_value, lcm_values[i])

        n = lcm_value // gcd(k, lcm_value)
        return n

    n = get_n(input_atoms, output_atoms, k)
    return str(n)

