    class ListNode:
        def __init__(self, val=0, next=None):
            self.val = val
            self.next = next

    def hasCycle(head: ListNode) -> bool:
        slow = head
        fast = head
        while fast and fast.next:
            slow = slow.next
            fast = fast.next.next
            if slow == fast:
                return True
        return False

    stdin_list = [int(x) for x in stdin.split()]
    n = stdin_list[0]
    head = ListNode()
    current = head
    for i in range(1, n + 1):
        current.next = ListNode(stdin_list[i])
        current = current.next
    pos = stdin_list[n + 1]

    if pos != -1:
        tail = head.next
        while tail.next:
            tail = tail.next
        current = tail.next = head.next
        for i in range(n - 1, pos, -1):
            current = current.next
        tail.next = current

    if hasCycle(head.next):
        return "true"
    else:
        return "false"

