    def distance(x1, y1, x2, y2):
        return ((x2 - x1) ** 2 + (y2 - y1) ** 2) ** 0.5

    d1 = distance(x, y, x1, y1)
    d2 = distance(x, y, x2, y2)
    d3 = distance(x, y, (x1 + x2) / 2, (y1 + y2) / 2)

    return str(min(d1, d2, d3))

