    max_shot = ""

    for row in range(R - K + 1):
        for col in range(S - K + 1):
            racket = ["." * col + "+" + "-" * K + "+" + "." * (S - col - K)]
            racket += ["|" + "*" * K + "|" + "*" * (S - K) if "*" in window[i][col:col + K] else "|" + "." * K + "|" + "*" * (S - K) for i in range(row, row + K)]
            racket += ["+" + "-" * K + "+" + "." * (S - col - K)]
            for i in range(row, row + K):
                window[i][col:col + K] = ["."] * K
            if "*" in racket[1:-1] and sum(row.count("*") for row in racket) == K:
                if max_flies < sum(row.count("*") for row in racket):
                    max_flies = sum(row.count("*") for row in racket)
                    max_shot = "\n".join(racket)

