def solution(stdin: str) -> str:
    # Read input
    N = int(stdin.readline().strip())
    words = [stdin.readline().strip() for _ in range(N)]

    # Find the index of the character '$' in the sentence
    dollar_index = None
    for i, word in enumerate(words):
        if '$' in word:
            dollar_index = i
            break

    # Replace the character '$' with the number of letters in the sentence
    sentence = ''.join(words)
    sentence = sentence.replace('$', str(len(sentence) - 1))

    # Convert the number to its named representation
    def number_to_words(number):
        ones = ["", "one", "two", "three", "four", "five", "six", "seven", "eight", "nine", "ten", "eleven", "twelve", "thirteen", "fourteen", "fifteen", "sixteen", "seventeen", "eighteen", "nineteen"]
        tens = ["", "", "twenty", "thirty", "forty", "fifty", "sixty", "seventy", "eighty", "ninety"]
        hundreds = ["", "onehundred", "twohundred", "threehundred", "fourhundred", "fivehundred", "sixhundred", "sevenhundred", "eighthundred", "ninehundred"]

        if number < 20:
            return ones[number]
        elif number < 100:
            return tens[number // 10] + ones[number % 10]
        elif number < 1000:
            return ones[number // 100] + "hundred" + (number_to_words(number % 100) if number % 100 else "")

    words[dollar_index] = number_to_words(len(sentence) - 1)

    # Return the final sentence
    return ' '.join(words)

