def solution(stdin: str) -> str:
    def is_safe(x, y, direction, visited):
        if x < 0 or x >= N or y < 0 or y >= M:
            return False
        if (x, y) in visited:
            return False
        if wall_maria[x][y] == '1':
            return False
        if wall_maria[x][y] == 'U' and direction != 'D':
            return False
        if wall_maria[x][y] == 'D' and direction != 'U':
            return False
        if wall_maria[x][y] == 'L' and direction != 'R':
            return False
        if wall_maria[x][y] == 'R' and direction != 'L':
            return False
        return True

    def bfs(start_x, start_y, t):
        queue = [(start_x, start_y)]
        visited = {(start_x, start_y)}
        depth = 0

        while queue:
            if depth + 1 > t:
                break
            for _ in range(len(queue)):
                x, y = queue.pop(0)
                if x == 0 or x == N - 1 or y == 0 or y == M - 1:
                    return depth + 1
                for direction in ['U', 'D', 'L', 'R']:
                    if direction == 'U':
                        new_x, new_y = x - 1, y
                    elif direction == 'D':
                        new_x, new_y = x + 1, y
                    elif direction == 'L':
                        new_x, new_y = x, y - 1
                    else:
                        new_x, new_y = x, y + 1
                    if is_safe(new_x, new_y, direction, visited):
                        queue.append((new_x, new_y))
                        visited.add((new_x, new_y))
            depth += 1

        return None

    t, N, M = map(int, stdin.readline().split())
    wall_maria = []
    for _ in range(N):
        wall_maria.append(stdin.readline().strip())

    for x in range(N):
        for y in range(M):
            if wall_maria[x][y] == 'S':
                start_x, start_y = x, y
                break

    result = bfs(start_x, start_y, t)
    if result is not None:
        return str(result)
    else:
        return "NOT POSSIBLE!"

