    balloons = [i for i in range(1, n + 1)]

    for h in helium:
        balloons.sort(key=lambda x: (x, h % x), reverse=True)
        for b in balloons:
            if b >= h:
                balloons.remove(b)
                break

    if not balloons:
        return "impossible"

    max_fraction = min(b / h for b, h in zip(balloons, helium))
    return f"{max_fraction:.6f}"

