        matrix[i][i] = -1

    def bitwise_and(a, b):
        return a & b

    for k in range(N):
        for i in range(N):
            for j in range(N):
                if matrix[i][j] == -1:
                    matrix[i][j] = bitwise_and(matrix[i][k], matrix[k][j])

    for i in range(N):
        for j in range(N):
            if matrix[i][j] == -1:
                matrix[i][j] = bitwise_and(i, j)

    result = [max(0, matrix[i][i]) for i in range(N)]
