    def area_triangle(a, b, c):
        s = (a + b + c) / 2
        return (s * (s - a) * (s - b) * (s - c)) ** 0.5

    N = int(stdin.readline())
    triangles = []
    for _ in range(N):
        a, b, c = map(float, stdin.readline().split())
        area = area_triangle(a, b, c)
        perimeter = a + b + c
        height = 2 * area / perimeter
        triangles.append((height, a, b))

    triangles.sort(reverse=True)
    L = 0
    for i, (height, a, b) in enumerate(triangles):
        if i == 0:
            L += a + b + height
        else:
            L = max(L, a + b + height)

    return format(L, ".4f")

