def solution(stdin: str) -> str:
    n, p, s, v = list(map(float, stdin.split()))

    def time_algorithm(c):
        return n * (log2(n))**(c * sqrt(2)) / (p * 10**9)

    def tour_length(c):
        return s * (1 + 1/c)

    def tour_time(c):
        return tour_length(c) / v

    def shortest_time(c):
        return time_algorithm(c) + tour_time(c)

    c = 1
    best_c = c
    best_time = shortest_time(c)

    while c <= n:
        current_time = shortest_time(c)
        if current_time < best_time:
            best_c = c
            best_time = current_time
        c += 0.0001

    return f"{best_time:.6f} {best_c:.6f}"

