        return "use the stairs"

    if g - s > f:
        return "use the stairs"

    if s + d*(f-g+1) >= g:
        return str((g-s)//u + (g-s)%u)

    if g - s <= d*g:
        return str(s//u + (s%u)%d)

    if g > s:
        return str(f//u - (g-s)//u + (g-s)%u)

    return str(f//d - (s-g)//d + (s-g)%u)

def solution(stdin: str) -> str:
    n = int(stdin.split("\n")[0])
    scores = sorted([int(score) for score in stdin.split("\n")[1:]], reverse=True)

    def group_score(scores: list) -> float:
        total_score = sum(scores)
        k = len(scores)
        return (1 / 5) * total_score * (1 + (4 / 5) ** (k - 1))

    def new_group_score(scores: list, index: int) -> float:
        new_scores = scores[:index] + scores[index+1:]
        return group_score(new_scores)

    current_group_score = group_score(scores)
    new_group_scores = [new_group_score(scores, i) for i in range(n)]
    average_new_group_score = sum(new_group_scores) / n

    return f"{current_group_score}\n{average_new_group_score}"

