def solution(stdin: str) -> str:
    n, m, s, d = map(int, stdin.split('\n')[0].split())
    current_fridge = list(map(int, stdin.split('\n')[1].split()))

    if sum(current_fridge) + n < m:
        return "impossible"

    def is_valid(refill_scheme):
        new_fridge = [refill_scheme[i] + current_fridge[i] for i in range(s)]
        served = 0
        for i in range(s):
            if new_fridge[i] > 0:
                served += 1
            if served >= m:
                return True
        return False

    def dfs(idx, remain, refill_scheme):
        if remain == 0:
            if is_valid(refill_scheme):
                return refill_scheme
            else:
                return None

        if idx == s:
            return None

        if current_fridge[idx] + remain >= d:
            refill_scheme[idx] = d - current_fridge[idx]
            return dfs(idx + 1, remain - refill_scheme[idx], refill_scheme)
        else:
            for i in range(current_fridge[idx] + 1, d + 1):
                refill_scheme[idx] = i - current_fridge[idx]
                result = dfs(idx + 1, remain - refill_scheme[idx], refill_scheme)
                if result is not None:
                    return result
            return None

    refill_scheme = [0] * s
    result = dfs(0, n, refill_scheme)

    if result is None:
        return "impossible"
    else:
        return " ".join(str(num) for num in result)

