            return 'g: ----------------------------------------------------------'
        elif line == 8:
            return 'f:                '
        elif line == 9:
            return 'e: ----------------------------------------------------------'
        elif line == 10:
            return 'd:                '
        elif line == 11:
            return 'c:                '
        elif line == 12:
            return 'b:                '
        elif line == 13:
            return 'a: ----------------------------------------------------------'

    for note in song:
        note_position = note_to_position[note[0].upper()]
        note_duration = note_to_duration.get(note[-1], 1)
